import {Component, ElementRef, OnDestroy, OnInit} from '@angular/core';
import {Subject, Subscription} from 'rxjs';
import {UserActionManager} from "./user-action-manager";

export enum EventType {
  PointerEvent,
  TouchEvent,
}

export interface LastCoordinates {
  lastX: number;
  lastY: number;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit, OnDestroy {
  public EventType = EventType;
  private pointerActive: boolean = false;
  public activeEventType$$: Subject<EventType> = new Subject<EventType>();
  private subscriptions: Subscription[] = [];

  private canvas: HTMLCanvasElement;
  private canvas2d;

  private lastCoordinates: LastCoordinates;

  constructor(private readonly ref: ElementRef) {}

  ngOnInit() {
    this.activeEventType$$.asObservable().subscribe((eventType: EventType) => {
      console.log('sub', eventType);
      eventType === EventType.PointerEvent
        ? this.addPointerEventListeners()
        : this.addTouchEventListeners();
    });
  }

  ngOnDestroy() {
    this.activeEventType$$.unsubscribe();
  }

  ngAfterViewInit() {
    this.canvas = document.querySelector('#Canvas');
    this.canvas2d = this.canvas.getContext('2d');
    this.canvas.width = window.innerWidth;
    this.canvas.height = window.innerHeight;
  }

  private addPointerEventListeners() {
    this.subscriptions.push(
        UserActionManager.getPointerDownEvent(this.ref.nativeElement).subscribe(
            val => {console.log("pointerDown", val)}
        )
    )

    this.subscriptions.push(
        UserActionManager.getPointerMoveEvent(this.ref.nativeElement).subscribe(
            val => {console.log("pointerMove", val)}
        )
    )

    this.subscriptions.push(
        UserActionManager.getPointerUpEvent(this.ref.nativeElement).subscribe(
            val => {console.log("pointerUp", val)}
        )
    )
  }

  private addTouchEventListeners(): void {
    this.subscriptions.push(
        UserActionManager.getTouchStartEvent(this.ref.nativeElement).subscribe(
            val => {console.log("touchStart", val)}
        )
    )

    this.subscriptions.push(
        UserActionManager.getTouchMoveEvent(this.ref.nativeElement).subscribe(
            val => {console.log("touchMove", val)}
        )
    )

    this.subscriptions.push(
        UserActionManager.getTouchEndEvent(this.ref.nativeElement).subscribe(
            val => {console.log("touchEnd", val)}
        )
    )
  }

  private removeAll

