import {filter, fromEvent, Observable} from "rxjs";

export class UserActionManager {


    public static getPointerDownEvent(doc: Document): Observable<Event> {
        return fromEvent(doc, "pointerdown")
    }

    public static getPointerMoveEvent(doc: Document) {
        return fromEvent(doc, "pointermove").pipe(
            filter((pointerMove: PointerEvent) => pointerMove.pressure > 0)
        )
    }

    public static getPointerUpEvent(doc: Document) {
        return fromEvent(doc, "pointerup")
    }
}
